mapper.insert:
insert into position (id, name, createDate,
  enabled, entity_id, startTime,
  did, endTime, createBy,
  updateDate, updateBy, addTime
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{createdate,jdbcType=TIMESTAMP},
  #{enabled,jdbcType=BIT}, #{entityId,jdbcType=INTEGER}, #{starttime,jdbcType=VARCHAR},
  #{did,jdbcType=INTEGER}, #{endtime,jdbcType=VARCHAR}, #{createby,jdbcType=VARCHAR},
  #{updatedate,jdbcType=TIMESTAMP}, #{updateby,jdbcType=VARCHAR}, #{addtime,jdbcType=VARCHAR}
  )

mapper.select:
SELECT de.*,d.name as dname from position de inner join department d on d.id = de.did INNER join (SELECT he.hotel_entity_id from pg_user_entity he INNER join user r on r.id =he.hr_id where r.id= #{uid}) pghe on pghe.hotel_entity_id =de.entity_id
where de.entity_id =#{entityId}
and de.name like '%${name}%'
